import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css"
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js"

const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {
        "text": "default"
      },
      context: {},
      boards: [],
      words: [],
      itemIds: false
    };
  }

  componentDidMount() {
    // TODO: set up event listeners

    monday.listen("settings", res => {
      console.log(res.data);
      this.getSettings(res);
      // {"fieldName": "fieldValue", "fieldName2": "fieldValue2"...}
    }); 

    monday.listen("context", res => {
      this.setState({context: res.data});
      console.log(res.data);
      monday.api(`boards (ids: $boardIds) { # board specific id
    		name
    		items  {
    				id
          	name
      			column_values (ids: date4) {
              text
              title
              type
      			  id
              value
      			}
    		}
    }' { variables: {boardIds: this.state.context.boardIds} }


      query ($boardIds: [Int]) { boards (ids:$boardIds) { name items(limit:1) { name column_values { title text } } } }`,
        { variables: {boardIds: this.state.context.boardIds} }
      )
      .then(res => {
        this.setState({boardData: res.data});
      });
    })

    // monday.listen("settings", this.getSettings);
  }

  // context \/






  // settings \/

  getSettings = (res) => {
    this.setState({ settings: res.data });
    console.log("settings!", res);
  };


  render() {
    return <div className="App">
      <AttentionBox
        title="Hello Monday Apps!"
        text={this.state.settings.text}
        type="success"
      />
    </div>;
  }
}

export default App;
