import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css"
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js"

const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {},
      context: {},
      boards: [],
      words: [],
      itemIds: false
    };
  }

  getSettings = (res) => {
    this.setState({ settings: res.data });
    console.log("settings!", res.data);
    this.generateWords();
  };

  getItemIds = (res) => {
    const itemIds = {};
    res.data.forEach((id) => (itemIds[id] = true));
    this.setState({ itemIds: itemIds });
    this.generateWords();
  };

  getContext = (res) => {
    const context = res.data;
    console.log("context!", context);
    this.setState({ context });

    const boardIds = context.boardIds || [context.boardId];
    monday
      .api(`query { boards(ids:[${boardIds}]) { id, items { id, name, column_values { id, text } } }}`)
      .then((res) => {
        this.setState({ boards: res.data.boards }, () => {
          console.log(res.data.boards[0].items.slice(0, 10).map((item) => item.id));
          this.generateWords();
        });
      });
  };

  // monday

  // monday.listen("context", res => {
  //   this.setState({context: res.data});
  //   console.log(res.data);
  //   monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items(limit:1) { name column_values { title text } } } }`,
  //     { variables: {boardIds: this.state.context.boardIds} }
  //   )
  //   .then(res => {
  //     this.setState({boardData: res.data});
  //   });
  // })


  setNum = () => {
    const num = 15;
    console.log("num", num)
    this.setState({num})
  }

  componentDidMount() {
    // TODO: set up event listeners

    monday.listen("settings", this.getSettings);
    monday.listen("context", this.getContext);
    monday.listen("itemIds", this.getItemIds);

    // monday.listen("settings", this.setNum);

    // monday.listen("context", res => {
    //   this.setState({context: res.data});
    //   console.log(res.data);
    //   monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items(limit:1) { name column_values { title text } } } }`,
    //     { variables: {boardIds: this.state.context.boardIds} }
    //   )
    //   .then(res => {
    //     this.setState({boardData: res.data});
    //   });
    // })
  }

  render() {
    return <div className="App">
      {/* <AttentionBox
        title="Hello Monday Apps!"
        text="hey"
        type="success"
      /> */}
      <h1>{ this.state.context }</h1>
      <span>{this.state.boardData}</span>
    </div>;
  }
}

export default App;
