let outDate;

manipulateDate = (inDate, range) => {
  range = parseInt(range);

  // convert yyyy-mm-dd (column text) to date object
  inDate = new Date(inDate);
  // create final date object (default current date)
  outDate = new Date();

  // calculate final date
  outDate.setDate(inDate.getDate() + range);

  // convert back to "yyyy-mm-dd"
  const outDate = outDate.toISOString().split("T")[0];

  // set the state value
  //   this.setState({ finalDateValue: finalDate });
  return outDate;
  //   console.log("manipulate date call", origDate, finalDate);
};
