    console.log("columnopts check: ", this.state.columnOptions);
  };

  // get the item dropdown options
  getItemOptions = () => {
    console.log("getItemOptions call");
    const options = this.state.boardData.boards[0].items;
    let arr = [];
    let itemLabel = "";
    let itemValue = "";

    for (let i = 0; i < options.length; i++) {
