import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js";
import TextField from "monday-ui-react-core/dist/TextField.js";

const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {
        text: "default",
        columnName: "date",
      },
      context: {},
      boards: [],
      dateIDS: [],
      dateColumns: [],
      itemIds: false,
    };
  }

  componentDidMount() {
    // TODO: set up event listeners

    monday.listen("settings", (res) => {
      console.log("settings early", res.data);
      this.getSettings(res);
      // {"fieldName": "fieldValue", "fieldName2": "fieldValue2"...}
    });

    monday.listen("settings", (res) => {
      console.log("update", res.data);
      this.updateDate();
      // {"fieldName": "fieldValue", "fieldName2": "fieldValue2"...}
    });

    monday.listen("context", (res) => {
      this.setState({ context: res.data });
      console.log("context early", res.data);
      monday
        .api(
          "query ($boardIds: [Int]) { boards (ids:$boardIds) { columns { id type title } } }",
          { variables: { boardIds: this.state.context.boardIds } }
        )
        .then((res) => {
          this.setState({ boardData: res.data });
          console.log("here!", res);
          this.getDateColumnInfo(res);
        });
    });
  }

  // context \/

  getDateColumnInfo = (res) => {
    console.log("detail", res.data.boards[0].columns);
    let dateIds = [];
    for (let i = 0; i < res.data.boards[0].columns.length; i++) {
      let tempPair = [];
      if (res.data.boards[0].columns[i].type === "date") {
        this.state.dateColumns.push(res.data.boards[0].columns[i].title);
        tempPair = [
          res.data.boards[0].columns[i].title,
          res.data.boards[0].columns[i].id,
        ];
        dateIds.push(tempPair);
      }
    }
    this.setState({ dateIDS: dateIds });
    console.log("id array", dateIds);
    console.log("date columns", this.state.dateColumns);

    // const st = dateIds;
    // return st;
  };

  updateDate = () => {
    monday.api(
      'mutation ($boardID: [Int], $itemID: [String], $columnID) change_column_value(board_id:$boardID, item_id: $itemID, column_id: $columnID, value: "{"date":"2022-07-21"}") { id } }',
      {
        variables: {
          boardID: this.state.context.boardIds,
          itemID: this.state.dateIDS[0],
        },
        columnID: this.state.dateColumns[0],
      }
    );
    console.log("end mutation");
  };

  // settings \/

  getSettings = (res) => {
    this.setState({ settings: res.data });
    console.log("settings!", res);
  };

  render() {
    return (
      <div className="App">
        <div style={{ textAlign: "center", width: "200%" }} className="subApp">
          <AttentionBox
            title={this.dateIDS}
            text="Let's start building your amazing app, which will change the world!"
            type="success"
          />
        </div>
        <h1> Pantry </h1>
        <div className="mainContent">
          <div className="subContent">
            <form onSubmit={formik.handleSubmit}>
              {/* Item Name */}
              <h5 htmlFor="name">Name</h5>
              <input
                id="name"
                name="name"
                type="inputField"
                onChange={formik.handleChange}
                value={
                  formik.values.name.charAt(0).toUpperCase() +
                  formik.values.name.slice(1)
                }
                onBlur={formik.handleBlur}
              />
              {formik.touched.name && formik.errors.name ? (
                <div className="error">{formik.errors.name}</div>
              ) : null}
              {/* Option */}
              <h5 htmlFor="option"> Option </h5>
              <div style={{ display: "inline-flex" }}>
                <label style={{ padding: "5px" }}>
                  <input
                    name="option"
                    type="radio"
                    onChange={() => formik.setFieldValue("option", true)}
                    onBlur={formik.handleBlur}
                  />
                  Add
                </label>
                <label style={{ padding: "5px" }}>
                  <input
                    name="option"
                    type="radio"
                    onChange={() => formik.setFieldValue("option", false)}
                    onBlur={formik.handleBlur}
                  />
                  Remove
                </label>
              </div>
              {formik.touched.option && formik.errors.option ? (
                <div className="error">{formik.errors.option}</div>
              ) : null}
              {/* Quantity */}
              <h5 htmlFor="quantity">Quantity</h5>
              <input
                id="quantity"
                name="quantity"
                type="number"
                onChange={formik.handleChange}
                value={formik.values.quantity}
                onBlur={formik.handleBlur}
              />
              {formik.touched.quantity && formik.errors.quantity ? (
                <div className="error">{formik.errors.quantity}</div>
              ) : null}
              {/* Category */}
              <h5 htmlFor="category">Category</h5>
              <select
                name="category"
                value={formik.values.category}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
              >
                <option value="">Select a category</option>
                <option value="Fruit">Fruit</option>
                <option value="Vegetable">Vegetable</option>
                <option value="Meat">Meat</option>
                <option value="Dairy">Dairy</option>
                <option value="Bakery">Bakery</option>
                <option value="Other">Other</option>
              </select>
              {formik.touched.category && formik.errors.category ? (
                <div className="error">{formik.errors.category}</div>
              ) : null}
              <div className="mainContent">
                <button type="submit">Submit</button>
              </div>
            </form>
          </div>
        </div>
        <div className="mainContent">
          <div
            style={{ textAlign: "right", width: "50%" }}
            className="subContent"
          >
            <select
              style={{ width: "100px", fontSize: "80%" }}
              value={sort}
              onChange={changeSort}
            >
              <option value="">Sort By</option>
              <option value="na">Name A-Z</option>
              <option value="qa">Quantity ↑</option>
              <option value="qd">Quantity ↓</option>
            </select>
          </div>
        </div>
        <div className="mainContent">
          <div style={{ width: "50%" }} className="subContent">
            <div className="subSubContent">
              <h3>Items</h3>
              {items.map((items) => (
                <div key={items.id}>
                  {items.name.charAt(0).toUpperCase() + items.name.slice(1)}
                </div>
              ))}
            </div>
            <div className="subSubContent">
              <h3>Quantity</h3>
              {items.map((items) => (
                <div key={items.id}>{items.quantity}</div>
              ))}
            </div>
            <div className="subSubContent">
              <h3>Category</h3>
              {items.map((items) => (
                <div key={items.id}>
                  {items.category.charAt(0).toUpperCase() +
                    items.category.slice(1)}
                </div>
              ))}
            </div>
            <button
              style={{
                marginTop: "1rem",
                padding: "0.02rem 0.8rem",
                fontSize: "80%",
                float: "right",
              }}
            >
              {" "}
              Delete All{" "}
            </button>
          </div>
        </div>
        <div
          style={{ textAlign: "right", width: "50%" }}
          className="subContent"
        >
          <TextField
            placeholder={this.state.settings.text}
            size={TextField.sizes.MEDIUM}
          />
        </div>
      </div>
    );
  }
}

export default App;
