   * @return id (string), name (string), column values: (column id (string), column title (string), type (string), text (string))
   */
  getItemInfo = (itemIdentity) => {
    monday
      .api(
        "query ($boardIds: [Int], $itemId: [Int]) { boards (ids:$boardIds) { name items (ids:$itemId) { name id column_values { id title type text } } } }",
        {
          variables: {
            boardIds: this.state.context.boardIds,
            itemId: parseInt(itemIdentity),
          },
        }
      )
      .then((res) => {
        console.log("getItemInfo call", res.data);
      });
  };

  getBoardOptions = () => {
    // this.getBoardData();

    monday
      .api("query ($boardIds: [Int]) { boards (ids:$boardIds) { name id } }", {
        variables: { boardIds: this.state.context.boardIds },
      })
      .then((res) => {
        this.setState({ boardData: res.data });
        console.log("get board options query", res.data);
      });

    const options = this.state.boardData.boards;
    let arr = [];
    let boardLabel = "";
    let boardValue = "";

    for (let i = 0; i < options.length; i++) {
      boardLabel = options[i].name;
      boardValue = options[i].id;
      arr.push({ label: boardLabel, value: boardValue });
    }

    this.setState({
      boardOptions: arr,
    });

    this.getColumnOptions();
  };

  getColumnOptions = () => {
    // this.getBoardData();

    monday
      .api(
        "query ($boardIds: [Int]) { boards (ids:$boardIds) { name id columns { id title }} }",
        {
          variables: { boardIds: this.state.context.boardIds },
        }
      )
      .then((res) => {
        this.setState({ columnData: res.data });
        console.log("get column options query", res.data);
      });

    const options = this.state.columnData.boards[0].columns;
    let arr = [];
    let columnLabel = "";
    let columnValue = "";

    for (let i = 0; i < options.length; i++) {
      columnLabel = options[i].title;
      columnValue = options[i].id;
      arr.push({ label: columnLabel, value: columnValue });
    }

    this.setState({
      columnOptions: arr,
    });
  };

