import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js";
import TextField from "monday-ui-react-core/dist/TextField.js";

const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {
        text: "default",
        columnName: "date",
      },
      context: {},
      boards: [],
      dateIDS: [],
      dateColumns: [],
      itemIds: false,
    };
  }

  componentDidMount() {
    // TODO: set up event listeners

    monday.listen("settings", (res) => {
      console.log("settings early", res.data);
      this.getSettings(res);
      // {"fieldName": "fieldValue", "fieldName2": "fieldValue2"...}
    });

    monday.listen("settings", (res) => {
      console.log("update", res.data);
      this.updateDate();
      // {"fieldName": "fieldValue", "fieldName2": "fieldValue2"...}
    });

    monday.listen("context", (res) => {
      this.setState({ context: res.data });
      console.log("context early", res.data);
      monday
        .api(
          "query ($boardIds: [Int]) { boards (ids:$boardIds) { columns { id type title } } }",
          { variables: { boardIds: this.state.context.boardIds } }
        )
        .then((res) => {
          this.setState({ boardData: res.data });
          console.log("here!", res);
          this.getDateColumnInfo(res);
        });
    });
  }

  // context \/

  getDateColumnInfo = (res) => {
    console.log("detail", res.data.boards[0].columns);
    let dateIds = [];
    for (let i = 0; i < res.data.boards[0].columns.length; i++) {
      let tempPair = [];
      if (res.data.boards[0].columns[i].type === "date") {
        this.state.dateColumns.push(res.data.boards[0].columns[i].title);
        tempPair = [
          res.data.boards[0].columns[i].title,
          res.data.boards[0].columns[i].id,
        ];
        dateIds.push(tempPair);
      }
    }
    this.setState({ dateIDS: dateIds });
    console.log("id array", dateIds);
    console.log("date columns", this.state.dateColumns);

    // const st = dateIds;
    // return st;
  };

  updateDate = () => {
    monday.api(
      'mutation ($boardID: [Int], $itemID: [String], $columnID) change_column_value(board_id:$boardID, item_id: $itemID, column_id: $columnID, value: "{"date":"2022-07-21"}") { id } }',
      {
        variables: {
          boardID: this.state.context.boardIds,
          itemID: this.state.dateIDS[0],
        },
        columnID: this.state.dateColumns[0],
      }
    );
    console.log("end mutation");
  };

  // settings \/

  getSettings = (res) => {
    this.setState({ settings: res.data });
    console.log("settings!", res);
  };

  render() {
    return (
      <div className="App">
        <div className="subApp">
          <AttentionBox
            title="Hello Monday Apps!"
            text="Let's start building your amazing app, which will change the world!"
            type="success"
          />
        </div>
        <div
          style={{ textAlign: "right", width: "50%" }}
          className="subContent"
        >
          <TextField placeholder="Meduim" size={TextField.sizes.MEDIUM} />
        </div>
      </div>
    );
  }
}

export default App;
