import React from "react";
import { useState, useEffect } from "react";
import "../styles/Selections.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";

import Dropdown from "monday-ui-react-core/dist/Dropdown.js";
import TextField from "monday-ui-react-core/dist/TextField.js";
import Button from "monday-ui-react-core/dist/Button.js";

const monday = mondaySdk();

const Selections = () => {
  const [context, setContext] = useState();
  const [boardData, setBoardData] = useState();
  const [sourceColumnOpts, setSourceColumnOpts] = useState();

  useEffect(() => {
    // Notice this method notifies the monday platform that user gains a first value in an app.
    // Read more about it here: https://developer.monday.com/apps/docs/mondayexecute#value-created-for-user/
    monday.execute("valueCreatedForUser");

    // TODO: set up event listeners, Here`s an example, read more here: https://developer.monday.com/apps/docs/mondaylisten/
    monday.listen("context", (res) => {
      setContext(res.data);
      console.log(res.data);
      // get board data and assign state value
      monday
        .api(
          "query ($boardIds: [Int]) { boards (ids:$boardIds) { name id columns { id title type } items { id name } } }",
          {
            variables: { boardIds: res.data.boardIds },
          }
        )
        .then((res) => {
          setBoardData(res.data);
          console.log("query finish: ", res.data.boards);
          getOptions(res);
          // this.getBoardOptions();
          // this.getDateColumnOptions();
          // this.getItemOptions();
        });
    });
  }, []);

  //Some example what you can do with context, read more here: https://developer.monday.com/apps/docs/mondayget#requesting-context-and-settings-data
  const attentionBoxText = `Hello, your user_id is: ${
    context ? context.user.id : "still loading"
  }.
  Let's start building your amazing app, which will change the world!`;

  const getOptions = (res) => {
    let arr = [];
    let options = res.data.boards[0].columns;
    console.log("source column array", options);
    for (let i = 0; i < options.length; i++) {
      // type "lookup" for mirror support
      if (options[i].type === "date" || options[i].type === "lookup") {
        arr.push({ label: options[i].title, value: options[i].id });
      }
    }
    setSourceColumnOpts(arr);
  };

  const renderButtons = () => {
    return (
      <div>
        <Button
          className="button-update"
          size={Button.sizes.MEDIUM}
          color={Button.colors.POSITIVE}
          // onClick={() => {
          //   this.updateAll();
          // }}
        >
          Update
        </Button>
        <Button
          className="button-submit"
          size={Button.sizes.MEDIUM}
          color={Button.colors.PRIMARY}
          // onClick={() => {
          //   this.updateDate();
          // }}
        >
          Submit
        </Button>
      </div>
    );
  };

  const renderDropdowns = () => {
    return (
      <div className="specificCell-options-dropdown">
        <Dropdown
          className="dropdown-board"
          placeholder={this.state.searchOptions.boardPrompt}
          noOptionsMessage={() => "hit update a few times"}
          size={Dropdown.size.MEDIUM}
          options={this.state.boardOptions}
          onOptionSelect={(e) => this.setBoardSelect(e)}
          // onOptionSelect={function noRefCheck() {
          //   console.log("dropdown selected");
          // }}
        />

        <Dropdown
          className="dropdown-sourceColumn"
          placeholder={this.state.searchOptions.sourceColumnPrompt}
          noOptionsMessage={() => "hit update a few times"}
          size={Dropdown.size.MEDIUM}
          options={this.state.columnOptions}
          onOptionSelect={(e) => this.setSourceColumnSelect(e)}
        />

        <Dropdown
          className="dropdown-targetColumn"
          placeholder={this.state.searchOptions.targetColumnPrompt}
          noOptionsMessage={() => "hit update a few times"}
          size={Dropdown.size.MEDIUM}
          options={this.state.columnOptions}
          onOptionSelect={(e) => this.setTargetColumnSelect(e)}
        />

        <Dropdown
          className="dropdown-item"
          placeholder={this.state.searchOptions.itemPrompt}
          noOptionsMessage={() => "hit update a few times"}
          size={Dropdown.size.MEDIUM}
          options={this.state.itemOptions}
          onOptionSelect={(e) => this.setItemSelect(e)}
        />
      </div>
    );
  };

  return (
    <div className="App">
      <Dropdown
        className="dropdown-board"
        placeholder={"Source Column"}
        noOptionsMessage={() => "hit update a few times"}
        size={Dropdown.size.MEDIUM}
        options={sourceColumnOpts}
        // onOptionSelect={(e) => this.setBoardSelect(e)}
        // onOptionSelect={function noRefCheck() {
        //   console.log("dropdown selected");
        // }}
      />
      <div>{renderButtons()}</div>
    </div>
  );
};

export default Selections;
