import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css"
//Explore more Monday React Components here: https://style.monday.com/
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js"

const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {
        "text": "default",
        "columnName": "date"
      },
      context: {},
      boards: [],
      dateIDS: [],
      itemIds: false
    };
  }

  componentDidMount() {
    // TODO: set up event listeners

    monday.listen("settings", res => {
      console.log(res.data);
      this.getSettings(res);
      // {"fieldName": "fieldValue", "fieldName2": "fieldValue2"...}
    }); 

    monday.listen("context", res => {
      this.setState({context: res.data});
      console.log(res.data);
      monday
        .api('query ($boardIds: [Int]) { boards (ids:$boardIds) { columns { id type title } } }',
        { variables: {boardIds: this.state.context.boardIds} }
        )
        .then(res => {
        this.setState({boardData: res.data});
        console.log("here!", res);
        this.getDateColumnIds(res);
      });
    })

    monday.listen("settings", this.createColumn());
  }

  // context \/


  getDateColumnIds = (res) => {
    console.log("detail", res.data.boards[0].columns);
    let dateIds = [];
    for (let i = 0; i < res.data.boards[0].columns.length; i++) {
      let tempPair = [];
      if (res.data.boards[0].columns[i].type === "date") {
        tempPair = [res.data.boards[0].columns[i].id, res.data.boards[0].columns[i].title];
        dateIds.push(tempPair);
      }
    }
    this.setState({dateIDS: dateIds});
    console.log("id array", dateIds);
    // const st = dateIds;
    // return st;
  }

  createColumn = (name) => {
    monday
        .api('mutation ($boardIds: [Int]) create_column(column_type: date, title: functionalDeadline, board_id: $boardIds)',
        { variables: {boardIds: this.state.context.boardIds, columnName: this.state.settings.columnName}}
        )
        console.log("end mutation");
  }

  // settings \/

  getSettings = (res) => {
    this.setState({ settings: res.data });
    console.log("settings!", res);
  };


  render() {
    return <div className="App">
      <AttentionBox
        title="data:"
        text={this.state.dateIDS}
        type="success"
      />

    </div>;
  }
}

export default App;
