import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
//Explore more Monday React Components here: https://style.monday.com/
import Dropdown from "monday-ui-react-core/dist/Dropdown.js";
import TextField from "monday-ui-react-core/dist/TextField.js";
import Button from "monday-ui-react-core/dist/Button.js";

const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {
        text: "Column Title",
        columnName: "date",
      },
      context: {},
      searchOptions: {
        boardPrompt: "Choose Board",
        boardOptions: [],
      },
      boardData: {},
      boards: [],
      dateIDS: [],
      dateColumns: [],
      itemIds: [],
    };
  }

  componentDidMount() {
    // TODO: set up event listeners

    // listens to changes in settings and sends to getSettings
    monday.listen("settings", (res) => {
      this.setState({ settings: res.data });
      console.log("settings listen", this.state.settings);
      // {"fieldName": "fieldValue", "fieldName2": "fieldValue2"...}
    });

    // listens to context
    monday.listen("context", (res) => {
      this.setState({ context: res.data });
      console.log("context listen", res.data);
      this.getBoardData();
    });
  }

  /* info getting functions */

  // get general board data
  getBoardData = () => {
    monday
      .api(
        "query ($boardIds: [Int]) { boards (ids:$boardIds) { name items { name id column_values { id title type text } } } }",
        { variables: { boardIds: this.state.context.boardIds } }
      )
      .then((res) => {
        this.setState({ boardData: res.data });
        console.log("getBoardData call", res.data);
      });
  };

  // basic retrieve item info by id
  /**
   * retrievs item info by id #
   * @param {*} res
   * @param {*} itemID
   * @return id (string), name (string), column values: (column id (string), column title (string), type (string), text (string))
   */
  getItemInfo = (itemIdentity) => {
    monday
      .api(
        "query ($boardIds: [Int], $itemId: [Int]) { boards (ids:$boardIds) { name items (ids:$itemId) { name id column_values { id title type text } } } }",
        {
          variables: {
            boardIds: this.state.context.boardIds,
            itemId: parseInt(itemIdentity),
          },
        }
      )
      .then((res) => {
        console.log("getItemInfo call", res.data);
      });
  };

  getBoardOptions = () => {
    this.getBoardData();
    const opts = this.state.boardData.boards;
    let arr = [];
    let boardName = "";
    let boardValue = "";

    for (let i = 0; i < opts.length(); i++) {
      boardName = opts.boards[i].name;
      boardValue = opts.boards[i];
      arr += { boardName, boardValue };
      console.log(arr);
    }

    return arr;
  };

  //get item by name
  // getItemIdFromName = () => {
  //   const items = this.state.boardData.boards;
  //   for (int i = 0)
  //   console.log("test", items);
  // };

  // context \/

  // getDateColumnInfo = (res) => {
  //   console.log("detail", res.data.boards[0].columns);
  //   let dateIds = [];
  //   for (let i = 0; i < res.data.boards[0].columns.length; i++) {
  //     let tempPair = [];
  //     if (res.data.boards[0].columns[i].type === "date") {
  //       this.state.dateColumns.push(res.data.boards[0].columns[i].title);
  //       tempPair = [
  //         res.data.boards[0].columns[i].title,
  //         res.data.boards[0].columns[i].id,
  //       ];
  //       dateIds.push(tempPair);
  //     }
  //   }
  //   this.setState({ dateIDS: dateIds });
  //   console.log("id array", dateIds);
  //   console.log("date columns", this.state.dateColumns);

  //   // const st = dateIds;
  //   // return st;
  // };

  functionTest = () => {
    console.log("function call");
    this.getBoardOptions();
    return [
      {
        value: "Red",
        label: "Red",
      },
    ];
  };

  comboTest = () => {
    const itId = this.state.boardData.boards[1].items[0].id;
    console.log("comboTest: ", itId);
    this.getItemInfo(itId);
  };

  // updateDate = () => {
  //   monday.api(
  //     'mutation ($boardID: [Int], $itemID: [String], $columnID) change_column_value(board_id:$boardID, item_id: $itemID, column_id: $columnID, value: "{"date":"2022-07-21"}") { id } }',
  //     {
  //       variables: {
  //         boardID: this.state.context.boardIds,
  //         itemID: this.state.dateIDS[0],
  //       },
  //       columnID: this.state.dateColumns[0],
  //     }
  //   );
  //   console.log("end mutation");
  // };

  render() {
    return (
      <div className="App">
        <div className="mainSection">
          <div className="header">
            <h1>enter info:</h1>
          </div>
          <div className="options">
            <div className="optionsField">
              <Dropdown
                placeholder={this.state.searchOptions.boardPrompt}
                size={Dropdown.size.MEDIUM}
                className="dropdown-stories-styles_spacing"
                options={this.functionTest()}
              />
            </div>
            <div className="optionsField">
              <TextField placeholder="Â± Days" size={TextField.sizes.MEDIUM} />
            </div>
            <div className="optionsField">
              <TextField
                placeholder="MM/DD/YYYY"
                size={TextField.sizes.MEDIUM}
                type={TextField.types.DATE}
              />
            </div>
            <div className="button">
              <Button
                size={Button.sizes.MEDIUM}
                color={Button.colors.POSITIVE}
                onClick={() => {
                  this.functionTest();
                }}
              >
                Update
              </Button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
